exports.run = (client, message, args) => {
  if (!message.member.hasPermission("MANAGE_ROLES"))
    return message.channel.send("**Yetkin yok.**");
  let member =
    message.mentions.members.first() || message.guild.members.get(args[0]);
  if (!member)
    return message.channel.send(
      "**Lütfen kullanıcıyı etiketleyiniz veya idsini yazınız.**"
    );
  if (rolid.match(/(\d{17,19})/g)) {
    member.roles.forEach(role => member.removeRole(role));
    member.addRole(rolid);
  } else member.roles.forEach(role => member.removeRole(role));
  return message.channel.send(
    `**Kullanıcının tüm rolleri ${
      rolid.match(/(\d{17,19})/g)
        ? `alınıp \`${message.guild.roles.get(rolid).name}\` rolü verildi.**`
        : "alındı.**"
    }`
  );
};
